import React, {useEffect, useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { updateStockData, clearStocks } from '../actions/stockdata';
import { findCompanies, clearCompanies } from '../actions/findcompanies';
import StockChart from './StockChart';
import PredictStock from './PredictStock';

const Landing = ({clearStocks, clearCompanies, updateStockData, findCompanies, stockdata: { x, y, line, loading, company_ticker, name },
   findcompanies: { companyTicker, loadingCompanies }}) => {

   const [ formData, setFormData ] = useState({
      company: 'apple',
      prediction: ''
   })
   const [ stockDataBlock, setStockDataBlock ] = useState('none')
   const [ frequency, setFrequency ] = useState('daily')
   const [ frequencyRadio, setFrequencyRadio ] = useState({
      daily:'true',
      monthly: null,
      yearly: null
   })
   const { company, prediction } = formData;
   const { daily, monthly, yearly } = frequencyRadio;

   const onChange = e => {
      setFormData({...formData, [e.target.name]: e.target.value});
   }
   console.log(monthly)
   const changeTimeSpan = frequency => {
      if (frequency === 'daily') {
         setFrequencyRadio({daily: 'true' });
         setFrequencyRadio({monthly: null });
         setFrequencyRadio({yearly: null });
      }
      else if (frequency === 'monthly') {
         setFrequencyRadio({daily: null });
         setFrequencyRadio({monthly: 'true' });
         setFrequencyRadio({yearly: null });
      }
      else if (frequency === 'yearly') {
         setFrequencyRadio({daily: null });
         setFrequencyRadio({monthly: null });
         setFrequencyRadio({yearly: 'true' });
      }
      setFrequency(frequency)
      clearStocks()
      updateStockData(companyTicker, frequency)
   }

   const onSubmit = e => {
      e.preventDefault();
      clearCompanies()
      findCompanies(company)
      clearStocks()
      setStockDataBlock('none')
   }

   useEffect(() => {
      if (!loadingCompanies && companyTicker != null) {
         updateStockData(companyTicker, frequency)
         setStockDataBlock('block')
      }
   }, [loadingCompanies])

   return (
      <div className='center-container'>
         <h1>Stock Prediction and Analysis</h1>
            <form>
              <input
                 type="text"
                 name="company"
                 value={company}
                 onChange={e => onChange(e)}
                 placeholder="Search.."
                 required />
              <button
                 className="button"
                 style={{verticalAlign:'middle'}}
                 onClick={e => onSubmit(e)}
                 ><span>Submit</span></button>
            </form>
         <div style={{fontSize: '15px', margin: '2%'}}><a href='https://money.cnn.com/data/dow30/' target="_blank">Only DOW 30 Companies</a></div>

         <div className='flex-container' style={{display:  stockDataBlock }}>
            <h6> Company: { name }</h6>
            <h6> NASDAQ: { company_ticker }</h6>
            {!loading && x != null && y != null && line != null ?
               (
                  <div>
                     <StockChart x={x} y={y} l={line}/>
                        <div style={{margin: '2%', display: 'inline-block'}}>Select a Time frame: </div>
                        <label className="container" style={{display: 'inline-block', marginRight: '2%'}}>Daily
                          <input
                             onChange={() => changeTimeSpan('daily')}
                             type="radio"
                             checked={daily}
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                        <label className="container" style={{display: 'inline-block',marginRight: '2%'}}>monthly
                          <input
                             onChange={() => changeTimeSpan('monthly')}
                             checked={monthly}
                             type="radio"
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                        <label className="container" style={{display: 'inline-block', marginRight: '2%'}}>Yearly
                          <input
                             onChange={() => changeTimeSpan('yearly')}
                             checked={yearly}
                             type="radio"
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                     <PredictStock />
                  </div>
               ) :
               (<div></div>)}
         </div>
      </div>
   )
}

Landing.propTypes = {
   updateStockData: PropTypes.func.isRequired,
   stockdata: PropTypes.object.isRequired,
   findCompanies: PropTypes.func.isRequired,
   findcompanies: PropTypes.object.isRequired,
   clearCompanies: PropTypes.func.isRequired,
   clearStocks: PropTypes.func.isRequired
}

const mapStateToProps = state => ({
   stockdata: state.stockdata,
   findcompanies: state.findcompanies
});

export default connect(mapStateToProps, { updateStockData, findCompanies, clearStocks, clearCompanies })(Landing);
