import React, {useEffect, useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { updateStockData } from '../actions/stockdata';
import { findCompanies } from '../actions/findcompanies';

import StockChart from './StockChart'

const Landing = ({updateStockData, findCompanies, stockdata: { x, y, line, loading, company_ticker, name },
   }) => {

   const [ formData, setFormData ] = useState({
      company: '',
      prediction: ''
   })

   const [ stockDataBlock, setStockDataBlock ] = useState('none')

   const { company, prediction } = formData;

   const onChange = e => {
      setFormData({...formData, [e.target.name]: e.target.value});
   }

   const onSubmit = e => {
      e.preventDefault();
      findCompanies(company)
      if (!loading && companyTicker != null) {
         updateStockData(companyTicker)
         setStockDataBlock('block')
      }

   }

   useEffect(() => {

   }, [0])

   return (
    <section className="landingPage">
      <div className='background-color'>
         <div className='center-container'>
            <h1>Stock Prediction and Analysis</h1>
               <form>
                 <input
                    type="text"
                    name="company"
                    value={company}
                    onChange={e => onChange(e)}
                    placeholder="Search.."
                    required />
                 <button
                    class="button"
                    style={{verticalAlign:'middle'}}
                    onClick={e => onSubmit(e)}
                    ><span>Hover</span></button>
               </form>
            <div style={{display:  stockDataBlock }}>
               <h6> Company: { name }</h6>
               <h6> NASDAQ: { company_ticker }</h6>
               {!loading && x != null && y != null && line != null ? (<StockChart x={x} y={y} l={line}/>) : (<div></div>)}
            </div>
         </div>
      </div>
    </section>
   )

}

Landing.propTypes = {
   updateStockData: PropTypes.func.isRequired,
   stockdata: PropTypes.object.isRequired,
   findCompanies: PropTypes.func.isRequired,
   findcompany: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
   stockdata: state.stockdata,
   findcompany: state.findcompanies
});

export default connect(mapStateToProps, { updateStockData, findCompanies })(Landing);
