import {
   UPDATE_COMPANY_NEWS,
   CLEAR_COMPANY_NEWS
} from '../actions/types';

const initialState = {
   companyName: null,
   loading: true
}

export default function(state = initialState, action) {
   const { type, payload } = action;
   switch(type) {
      case UPDATE_COMPANY_NEWS:
         return {
            ...state,
            companyName: payload.name,
            loading: false
         }
      case CLEAR_COMPANY_NEWS:
         return {
            ...state,
            companyName: null,
            loading: true
         }
      default:
         return state;
   }
}
