import React, {useEffect, useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

const Landing = () => {

   const [ formData, setFormData ] = useState({
      company: 'apple',
      prediction: ''
   })

   const { comapny } = formData

   const onChange = e => {
      setFormData({...formData, [e.target.name]: e.target.value});
   }

   const onSubmit = () => (<StockResults company={company} />)

   return (
      <div className='center-container'>
         <h1>Stock Prediction and Analysis</h1>
            <form>
              <input
                 type="text"
                 name="company"
                 value={company}
                 onChange={e => onChange(e)}
                 placeholder="Search.."
                 required />
              <button
                 className="button"
                 style={{verticalAlign:'middle'}}
                 onClick={() => onSubmit()}
                 ><span>Submit</span></button>
            </form>
         <div style={{fontSize: '15px', margin: '2%'}}><a href='https://money.cnn.com/data/dow30/' target="_blank">Only DOW 30 Companies</a></div>
         <StockResults />
         <div className='flex-container' style={{display:  stockDataBlock }}>
            <h6> Company: { name }</h6>
            <h6> NASDAQ: { company_ticker }</h6>
            {!loading && x != null && y != null && line != null ?
               (
                  <div>
                     <StockChart x={x} y={y} l={line}/>
                        <div style={{margin: '2%', display: 'inline-block'}}>Select a Time frame: </div>
                        <label className="container" style={{display: 'inline-block', marginRight: '2%'}}>Daily
                          <input
                             onChange={() => changeTimeSpan('daily')}
                             type="radio"
                             checked={daily}
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                        <label className="container" style={{display: 'inline-block',marginRight: '2%'}}>monthly
                          <input
                             onChange={() => changeTimeSpan('monthly')}
                             checked={monthly}
                             type="radio"
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                        <label className="container" style={{display: 'inline-block', marginRight: '2%'}}>Yearly
                          <input
                             onChange={() => changeTimeSpan('yearly')}
                             checked={yearly}
                             type="radio"
                             name="radio"/>
                          <span className="checkmark"></span>
                        </label>
                     <PredictStock />
                  </div>
               ) :
               (<div></div>)}
         </div>
      </div>
   )
}

Landing.propTypes = {

}

const mapStateToProps = state => ({

});

export default connect(mapStateToProps, { })(Landing);
