import React, {useEffect} from 'react';
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { updateStockData } from '../actions/stockdata';

import StockChart from './StockChart'

const Landing = ({updateStockData, stockdata: {x, y, line, loading, company_ticker, name}}) => {

   useEffect(() => {
      updateStockData()
   }, [0])

   return (
    <section className="landingPage">
      <div className='center-container'>
         <h1>Stock Prediction and Analysis</h1>
            <form>
              <input type="text" name="search" placeholder="Search.."/>
              <button class="button" style={{verticalAlign:'middle'}}><span>Hover</span></button>
            </form>
         <h6>Company: { name }</h6>
         <h6 style={{display:'inline-block'}}>NASDAQ: { company_ticker }</h6>
         {!loading && x != null && y != null && line != null ? (<StockChart x={x} y={y} l={line}/>) : (<div></div>)}
      </div>
    </section>
   )
}

Landing.propTypes = {
   updateStockData: PropTypes.func.isRequired,
   stockdata: PropTypes.object.isRequired
}

const mapStateToProps = state => ({
   stockdata: state.stockdata
});

export default connect(mapStateToProps, { updateStockData })(Landing);
