import React, {useEffect} from 'react';
import Chart from 'chart.js'

const StockChart = ({x, y, l}) => {

   useEffect(() => {
      console.log(x)
      console.log(y)
      console.log(l)
      var ctx = document.getElementById('myChart').getContext('2d');

      var mixedChart = new Chart(ctx, {
         type: 'bar',
         data: {
            datasets: [{
               label: 'SVM Model',
               data: l,
               type: 'line',
               fill: false,
               backgroundColor: '#FFE400',
               borderColor: '#FFE400'
            },{
               label: 'Stock Data',
               data: y,
               backgroundColor: '#FF652F',
               borderColor: '#FF652F'
            }],

            labels: x
         },

         options: {
            scales: {
               xAxes: [{
                   ticks: { fontColor: 'white', fontSize: 15 },
                   type: 'time',
                   time: { displayFormats: { week: '11' }},
                   gridLines: {
                      color: 'black'
                   }
              }],
              yAxes: [{
                 ticks: { fontColor: 'white', fontSize: 15 },
                 gridLines: {
                    color: 'black'
                 }
              }]
           }
        }
   });

   }, [0])


   return (
    <section className="graph">
      <div className='StockChart' style={{height: '500px', width: '1000px'}} >
         <canvas id="myChart"></canvas>
      </div>
    </section>
   )
}

export default StockChart;
