import axios from 'axios';

import {
   UPDATE_STOCK_DATA,
   FIND_COMPANY
} from './types';


export const updateStockData = () => async dispatch => {
   try {
      const res = await axios.get('/stockprices');
      dispatch({
         type: UPDATE_STOCK_DATA,
         payload: res.data.data
      });
   } catch(err) {
      console.log(err)
   }
}

export const findCompanies = (company) => async dispatch => {
   try {
      const res = await axios.get('/findCompanies');
      dispatch({
         type: FIND_COMPANY,
         payload: res.data.data
      });
   } catch(err) {
      console.log(err)
   }
}
